import{_ as h,r as n,o as t,c as d,d as e,a as s,f as i,e as a}from"./app.4f607c78.js";const p={},o=i('<h1 id="\u8881\u957F\u5E9A\u8001\u5E08\u4E3A\u65B0\u751F\u63A8\u8350\u7684\u4E66\u5355-2018" tabindex="-1"><a class="header-anchor" href="#\u8881\u957F\u5E9A\u8001\u5E08\u4E3A\u65B0\u751F\u63A8\u8350\u7684\u4E66\u5355-2018" aria-hidden="true">#</a> \u8881\u957F\u5E9A\u8001\u5E08\u4E3A\u65B0\u751F\u63A8\u8350\u7684\u4E66\u5355\uFF082018\uFF09</h1><p>\u4F5C\u4E3A\u4E00\u540D\u7406\u5DE5\u79D1\u5927\u5B66\u91CC\u7684\u6587\u79D1\u6559\u5E08\uFF0C\u5E38\u88AB\u8981\u6C42\u201C\u63A8\u8350\u51E0\u672C\u597D\u4E66\u201D\u3002\u5728\u4E00\u4E2A\u8D44\u8BAF\u5982\u6B64\u53D1\u8FBE\uFF0C\u8D44\u6E90\u83B7\u53D6\u6210\u672C\u5982\u6B64\u4F4E\u5EC9\u7684\u65F6\u4EE3\uFF0C\u6211\u7406\u89E3\u4E0A\u8FF0\u8981\u6C42\u4E4B\u4E2D\u9690\u542B\u7740\u4E00\u79CD\u4FE1\u4EFB\u3002\u4F46\u6709\u65F6\u53C8\u4E0D\u786E\u4FE1\uFF0C\u8FD9\u79CD\u6240\u8C13\u4FE1\u4EFB\u662F\u5426\u53EA\u662F\u5BA2\u5957\u751A\u6216\u61D2\u60F0\u7684\u8868\u73B0\u3002\u56DE\u60F3\u8FC7\u5F80\u4E24\u5E74\u95F4\uFF0C\u6211\u51E0\u4E4E\u6CA1\u6709\u8BF4\u670D\u4EFB\u4F55\u4E00\u540D\u540C\u5B66\u57F9\u517B\u8D77\u9605\u8BFB\u7684\u4E60\u60EF\uFF08\u5F88\u591A\u540C\u5B66\u7231\u8BFB\u4E66\uFF0C\u6E90\u4E8E\u5BB6\u5EAD\u6216\u57FA\u7840\u6559\u80B2\u9636\u6BB5\u7684\u57F9\u517B\uFF0C\u4E0E\u6211\u65E0\u5173\uFF09\uFF0C\u751A\u81F3\u6108\u53D1\u6CA1\u6709\u5E95\u6C14\u53BB\u9610\u8FF0\u9605\u8BFB\u7684\u610F\u4E49\u548C\u5FC5\u8981\u6027\u3002\u53BB\u5E74\u79CB\u5B63\u8BA4\u8BA4\u771F\u771F\u7ED9\u540C\u5B66\u4EEC\u63A8\u8350\u7684\u4E66\uFF0C\u4E0D\u77E5\u591A\u5C11\u4EBA\u5DF2\u8BFB\u5B8C\u3002\uFF08\u5FF5\u53CA\u6B64\uFF0C\u6211\u4ECD\u65E7\u6000\u5FF5\u4E00\u4E2A\u53EB\u505A\u201C\u9E92\u9E9F\u4E66\u5427\u201D\u7684\u5730\u65B9\u3002\u5F53\u5E74\u7684\u8521\u5E97\u957F\u8BA4\u771F\u5730\u628A\u4E66\u5355\u4E2D\u7684\u4F5C\u54C1\u90FD\u8C03\u6765\uFF0C\u6446\u5728\u4E13\u95E8\u7684\u67B6\u4E0A\uFF0C\u79CB\u51AC\u6625\u590F\uFF0C\u8D70\u4E86\u5B8C\u6574\u7684\u4E00\u4E2A\u8F6E\u56DE\uFF09\u4E5F\u7F62\uFF0C\u6B63\u662F\u56E0\u4E3A\u8FD9\u6837\u65E0\u671F\u8BB8\u3001\u975E\u529F\u5229\uFF0C\u4E8B\u60C5\u53CD\u5012\u5355\u7EAF\u8D77\u6765\u30022018\u5E74\u79CB\u5B63\u6CA1\u6709\u5F00\u8BFE\uFF0C\u4ECD\u7136\u5217\u51E0\u672C\u4E66\uFF0C\u7B97\u662F\u5BF9\u201C\u5DE5\u4F5C\u91CF\u201D\u7F3A\u5931\u7684\u5C0F\u5C0F\u5F25\u8865\u3002</p><p>\u4ECA\u5E74\u4E66\u5355\u7684\u6838\u5FC3\u7406\u5FF5\u662F\u201C\u54C1\u6027\u4E0E\u4FEE\u517B\u201D\uFF0C\u6216\u66F4\u76F4\u767D\u4E00\u70B9\u8BF4\uFF0C\u662F\u201C\u5982\u4F55\u5728\u4E00\u4E2A\u53D8\u5E7B\u4E0D\u5C45\u7684\u65F6\u4EE3\u6709\u6240\u6301\u5B88\u201D\u3002\u4E0D\u6D88\u8BF4\uFF0C\u8FD9\u6837\u7684\u7ACB\u610F\u5C31\u5DF2\u7ECF\u51B3\u5B9A\u4E86\u4E66\u5355\u672C\u8EAB\u4E0E\u5F00\u5217\u8005\u81EA\u5DF1\u7684\u4EF7\u503C\u89C2\u5FF5\u6DF1\u5207\u7EA0\u7F20\u3002\u4F46\u662F\uFF0C\u4E0E\u5176\u677F\u8D77\u9762\u5B54\u88C5\u4F5C\u5BA2\u89C2\u516C\u6B63\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u4EBA\u6587\u5B66\u8005\uFF0C\u6211\u66F4\u613F\u610F\u5728\u8D77\u521D\u5C31\u4EAE\u660E\u5E95\u7EBF\u3001\u539F\u5219\u548C\u53D6\u820D\u3002\u6211\u4EEC\u6240\u5904\u7684\u65F6\u4EE3\uFF0C\u5DF2\u7ECF\u7ECF\u4E0D\u8D77\u548C\u7A00\u6CE5\u3001\u6253\u5706\u573A\uFF0C\u800C\u9AD8\u7B49\u6559\u80B2\u672C\u8EAB\u7684\u7406\u60F3\u4E2D\u672C\u5C31\u5305\u542B\u7740\u4EBA\u751F\u89C2\u3001\u4E16\u754C\u89C2\u3001\u4EF7\u503C\u89C2\u7684\u5F15\u5BFC\u3002\u5728\u6211\u4EEC\u88AB\u79FB\u52A8\u7EC8\u7AEF\u548C\u4FE1\u606F\u5DE8\u6D41\u6240\u88F9\u631F\u7684\u751F\u6D3B\u91CC\uFF0C\u77E5\u8BC6\u548C\u6280\u80FD\u7684\u79EF\u7D2F\u4E0D\u96BE\uFF0C\u4F46\u5FC3\u5883\u548C\u64CD\u5B88\u7684\u953B\u94F8\u5374\u4E0D\u6613\u3002</p><p>\u4EE5\u4E0B\u7F57\u5217\u4F5C\u54C1\uFF0C\u90FD\u662F\u6211\u66FE\u7ECF\u8BFB\u8FC7\u751A\u81F3\u81F3\u4ECA\u8FD8\u5E38\u5E38\u91CD\u8BFB\u7684\u4F5C\u54C1\u3002\u6211\u6709\u610F\u8BC6\u5730\u653E\u8FDB\u4E00\u4E9B\u5927\u5BB6\u5199\u7684\u201C\u5C0F\u4E66\u201D\u3002\u6240\u8C13\u201C\u5C0F\u201D\u8005\uFF0C\u53EA\u662F\u89C4\u6A21\u7684\u7CBE\u81F4\uFF0C\u7EDD\u4E0D\u610F\u5473\u89C6\u91CE\u7684\u77ED\u6D45\u6216\u54C1\u5473\u7684\u72ED\u4FC3\u3002\u76F8\u53CD\uFF0C\u5728\u6709\u9650\u7684\u7A7A\u95F4\u5185\uFF0C\u5728\u9AD8\u5EA6\u526A\u88C1\u7684\u6750\u6599\u4E2D\uFF0C\u5F80\u5F80\u80FD\u89C1\u771F\u6B63\u9AD8\u8FDC\u7684\u89C1\u8BC6\u3002\u4E0E\u53BB\u5E74\u76F8\u4F3C\uFF0C\u4E66\u5355\u4E2D\u4ECD\u5C31\u6709\u4E5D\u672C\u4E66\uFF0C\u4EE5\u6BCF\u592930-50\u9875\u7684\u9605\u8BFB\u91CF\u8BA1\u7B97\uFF0C\u5927\u7EA6\u4E00\u4E2A\u5B66\u671F\u4FBF\u53EF\u8BFB\u5B8C\u3002</p><p><img src="https://mirrors.sustech.edu.cn/git/sustech-online/sustech-online-ng/-/raw/master/docs/study/book-list-by-prof-yuan/2018/1.jpeg" alt="\u5C01\u9762"></p><h2 id="\u300A\u955C\u5B50-\u7167\u51FA\u4F60\u770B\u4E0D\u89C1\u7684\u4E16\u754C\u53F2\u300B-\u7231\u5FB7\u534E\u591A\xB7\u52A0\u83B1\u4E9A\u8BFA\u8457-\u5E7F\u897F\u5E08\u8303\u5927\u5B66\u51FA\u7248\u793E2012\u5E74" tabindex="-1"><a class="header-anchor" href="#\u300A\u955C\u5B50-\u7167\u51FA\u4F60\u770B\u4E0D\u89C1\u7684\u4E16\u754C\u53F2\u300B-\u7231\u5FB7\u534E\u591A\xB7\u52A0\u83B1\u4E9A\u8BFA\u8457-\u5E7F\u897F\u5E08\u8303\u5927\u5B66\u51FA\u7248\u793E2012\u5E74" aria-hidden="true">#</a> \u300A\u955C\u5B50\uFF1A\u7167\u51FA\u4F60\u770B\u4E0D\u89C1\u7684\u4E16\u754C\u53F2\u300B\uFF0C\u7231\u5FB7\u534E\u591A\xB7\u52A0\u83B1\u4E9A\u8BFA\u8457\uFF0C\u5E7F\u897F\u5E08\u8303\u5927\u5B66\u51FA\u7248\u793E2012\u5E74</h2><p>\u6CA1\u6709\u54EA\u672C\u8457\u4F5C\u6BD4\u300A\u62C9\u4E01\u7F8E\u6D32\u88AB\u5207\u5F00\u7684\u8840\u7BA1\u300B\u66F4\u6DF1\u523B\u5730\u5199\u51FA\u4E86\u5357\u7F8E\u5927\u9646\u5728\u8FC7\u5F80\u56DB\u767E\u5E74\u95F4\u6240\u906D\u53D7\u7684\u82E6\u96BE\uFF0C\u5728\u6218\u706B\u6A2A\u98DE\u3001\u72EC\u88C1\u5F53\u9053\u7684\u5E74\u6708\u91CC\uFF0C\u4EBA\u4EEC\u628A\u5B83\u548C\u5A74\u513F\u7684\u8941\u8913\u4E00\u8D77\u5E26\u5728\u8EAB\u4E0A\uFF0C\u4E0D\u820D\u5F97\u4E22\u5F03\u3002\u8FD9\u672C\u6770\u4F5C\u7684\u4F5C\u8005\u6B63\u662F\u4E4C\u62C9\u572D\u8BB0\u8005\u7231\u5FB7\u534E\u591A\xB7\u52A0\u83B1\u4E9A\u8BFA\u3002\u4F5C\u4E3A\u7B2C\u4E09\u4E16\u754C\u516C\u5171\u77E5\u8BC6\u5206\u5B50\u7684\u5178\u8303\uFF0C\u52A0\u83B1\u4E9A\u8BFA\u4E00\u76F4\u4FDD\u6301\u7740\u654F\u9510\u7684\u6279\u5224\u89C6\u89D2\uFF0C\u5BF9\u4E0D\u516C\u6B63\u7684\u4E16\u754C\u4F53\u7CFB\u5F3A\u52A0\u7ED9\u540C\u80DE\u7684\u75DB\u82E6\u5145\u6EE1\u6124\u61D1\u3002</p><p>\u5E02\u9762\u4E0A\u5173\u4E8E\u4E16\u754C\u53F2\u7684\u4E66\u7C4D\u8FD1\u5E74\u6765\u6C57\u725B\u5145\u680B\uFF0C\u8FD9\u5176\u4E2D\u9690\u542B\u7740\u5927\u56FD\u5D1B\u8D77\u540E\u7684\u4E2D\u56FD\u4EBA\u5BF9\u81EA\u6211\u8BA4\u540C\u7684\u67D0\u79CD\u7126\u8651\u3002\u6211\u4EEC\u5BF9\u5168\u7403\u5386\u53F2\u7684\u8FFD\u95EE\uFF0C\u6839\u6E90\u4E0A\u6050\u6015\u8FD8\u5728\u201C\u4E2D\u56FD-\u4E16\u754C\u201D\u4E24\u6781\u4E4B\u95F4\u6240\u4EA7\u751F\u7684\u5F20\u529B\u3002\u6070\u56E0\u5982\u6B64\uFF0C\u516C\u6B63\u7684\u5386\u53F2\u89C2\u5FF5\u6BD4\u4E8B\u4EF6\u7F57\u5217\u3001\u9038\u95FB\u52FE\u6C89\u66F4\u4E3A\u91CD\u8981\u3002\u514B\u7F57\u9F50\u6709\u8A00\uFF0C\u201C\u4E00\u5207\u5386\u53F2\u90FD\u662F\u5F53\u4EE3\u53F2\u201D\uFF0C\u5386\u53F2\u7684\u9610\u91CA\u7EC8\u7A76\u4F1A\u8FDB\u5165\u6211\u4EEC\u5F53\u4E0B\u6240\u601D\u6240\u611F\uFF0C\u5E76\u751F\u4EA7\u51FA\u60F3\u8C61\u672A\u6765\u7684\u84DD\u56FE\u3002\u52A0\u83B1\u4E9A\u8BFA\u7684\u8FD9\u672C\u4E66\uFF0C\u5C55\u793A\u7684\u662F\u6210\u738B\u8D25\u5BC7\u7684\u6B63\u7EDF\u5386\u53F2\u4E66\u5199\u7684\u80CC\u9762\uFF0C\u4FDD\u62A4\u7684\u662F\u4E8C\u5341\u4E16\u7EAA\u89E3\u653E\u601D\u6F6E\u7684\u5386\u53F2\u9057\u4EA7\u3002\u63A8\u8350\u5B83\uFF0C\u662F\u60F3\u8BA9\u5927\u5BB6\u4ECD\u7136\u80FD\u4FDD\u6709\u6734\u7D20\u7684\u6B63\u4E49\u611F\u548C\u5BF9\u4EBA\u7C7B\u547D\u8FD0\u4F11\u621A\u4E0E\u5171\u7684\u8BA4\u77E5\u7ACB\u573A\u3002</p><h2 id="\u300A\u4E07\u5386\u5341\u4E94\u5E74\u300B-\u9EC4\u4EC1\u5B87\u8457-\u4E2D\u534E\u4E66\u5C402006\u5E74" tabindex="-1"><a class="header-anchor" href="#\u300A\u4E07\u5386\u5341\u4E94\u5E74\u300B-\u9EC4\u4EC1\u5B87\u8457-\u4E2D\u534E\u4E66\u5C402006\u5E74" aria-hidden="true">#</a> \u300A\u4E07\u5386\u5341\u4E94\u5E74\u300B\uFF0C\u9EC4\u4EC1\u5B87\u8457\uFF0C\u4E2D\u534E\u4E66\u5C402006\u5E74</h2><p>\u81EA1982\u5E74\u88AB\u8BD1\u4ECB\u81F3\u4E2D\u56FD\u5927\u9646\u4EE5\u6765\uFF0C\u6B64\u4E66\u5728\u6570\u5341\u5E74\u95F4\u7A76\u7ADF\u88AB\u591A\u5C11\u4EBA\u8BFB\u8FC7\u7231\u8FC7\uFF0C\u96BE\u4EE5\u7EDF\u8BA1\u3002\u9EC4\u4EC1\u5B87\u5148\u751F\u65E9\u5E74\u662F\u5357\u5F00\u7684\u5B66\u751F\uFF0C\u6297\u6218\u65F6\u671F\u505A\u8FC7\u8FDC\u5F81\u519B\u7684\u4E0A\u5C09\u53C2\u8C0B\uFF0C\u540E\u6765\u5728\u5BC6\u6B47\u6839\u5927\u5B66\u5FF5\u4E86\u5386\u53F2\u5B66\u7684\u535A\u58EB\u3002\u4ED6\u662F\u5FC3\u6000\u201C\u5927\u5386\u53F2\u201D\u89C2\u5FF5\u7684\u6700\u540E\u4E00\u4EE3\u5B66\u4EBA\u3002\u4E0E\u4ECA\u65E5\u6761\u5757\u5206\u660E\uFF0C\u9AD8\u5EA6\u4E13\u4E1A\u5316\u7684\u4E13\u4E1A\u53F2\u5B66\u7814\u7A76\u4E0D\u540C\uFF0C\u5176\u6240\u5173\u5FC3\u7684\u662F\u5982\u4F55\u4ECE\u6574\u4F53\u4E0A\u7406\u89E3\u4E2D\u56FD\u8FD1\u4EE3\u4EE5\u6765\u7684\u53D8\u8FC1\u3002\u524D\u51E0\u5E74\u300A\u660E\u671D\u90A3\u4E9B\u4E8B\u513F\u300B\u98CE\u9761\u4E00\u65F6\uFF0C\u91CC\u9762\u8BB2\u6211\u4EEC\u5F53\u4EE3\u4E2D\u56FD\u4EBA\u5C31\u67D0\u79CD\u610F\u4E49\u800C\u8A00\u4ECD\u7136\u751F\u6D3B\u5728\u660E\u671D\u7684\u6F2B\u957F\u6295\u5F71\u4E4B\u4E2D\u3002\u8FD9\u79CD\u5C06\u660E\u4EE3\u89C6\u4F5C\u5E7F\u4E49\u4E4B\u201C\u8FD1\u4EE3\u201D\u8D77\u70B9\u7684\u53F2\u89C2\uFF0C\u540C\u6837\u662F\u9EC4\u5148\u751F\u672C\u4E66\u7684\u9898\u773C\u3002</p><p>\u5927\u5BB6\u5E76\u975E\u4E13\u4E1A\u53F2\u5B66\u7814\u7A76\u8005\uFF0C\u82E5\u60F3\u5B9E\u73B0\u201C\u8BFB\u53F2\u4F7F\u4EBA\u660E\u667A\u201D\uFF0C\u91CD\u8981\u7684\u5373\u5728\u4E8E\u628A\u63E1\u5386\u53F2\u5174\u8870\u7684\u5B8F\u89C2\u8109\u7EDC\u548C\u590D\u6742\u673A\u7406\uFF0C\u800C\u4E0D\u662F\u6C89\u8FF7\u4E8E\u91CE\u53F2\u9038\u95FB\u7684\u65E0\u804A\u8C08\u8D44\u3002\u5C31\u8FD9\u4E00\u70B9\u800C\u8A00\uFF0C\u5E02\u9762\u4E0A\u6D41\u884C\u7684\u4E0D\u5C11\u53F2\u4E66\uFF0C\u5176\u5B9E\u4E0D\u8BFB\u4E5F\u7F62\u3002\u63A8\u8350\u9EC4\u5148\u751F\uFF0C\u63A8\u8350\u7684\u662F\u4E00\u79CD\u89C6\u91CE\u548C\u60C5\u6000\uFF0C\u4E00\u79CD\u6073\u5207\u7684\u3001\u9971\u542B\u60C5\u611F\u7684\u8FDB\u5165\u5386\u53F2\u7684\u65B9\u5F0F\u3002\u9664\u672C\u4E66\u4E4B\u5916\uFF0C\u9EC4\u4EC1\u5B87\u53E6\u6709\u300A\u4E2D\u56FD\u5927\u5386\u53F2\u300B\u3001\u300A\u8D6B\u900A\u6CB3\u7554\u8C08\u4E2D\u56FD\u5386\u53F2\u300B\uFF0C\u90FD\u503C\u5F97\u4E00\u9605\u3002</p><h2 id="\u300A\u7B11\u8C08\u5927\u5148\u751F\u300B-\u9648\u4E39\u9752\u8457-\u5E7F\u897F\u5E08\u8303\u5927\u5B66\u51FA\u7248\u793E2011\u5E74" tabindex="-1"><a class="header-anchor" href="#\u300A\u7B11\u8C08\u5927\u5148\u751F\u300B-\u9648\u4E39\u9752\u8457-\u5E7F\u897F\u5E08\u8303\u5927\u5B66\u51FA\u7248\u793E2011\u5E74" aria-hidden="true">#</a> \u300A\u7B11\u8C08\u5927\u5148\u751F\u300B\uFF0C\u9648\u4E39\u9752\u8457\uFF0C\u5E7F\u897F\u5E08\u8303\u5927\u5B66\u51FA\u7248\u793E2011\u5E74</h2><p>\u201C\u53E4\u6015\u6587\u8A00\u6587\uFF0C\u4ECA\u6015\u5468\u6811\u4EBA\u201D\uFF0C\u5728\u4E2D\u56FD\u5927\u9646\u63A5\u53D7\u8FC7\u521D\u7EA7\u6559\u80B2\u7684\u4EBA\u5BF9\u6B64\u6050\u6015\u591A\u6709\u4F1A\u5FC3\u4E00\u7B11\u3002\u4E3B\u7BA1\u90E8\u95E8\u9887\u6709\u9ED8\u5951\u5730\u8FDE\u5E74\u524A\u51CF\u7740\u9C81\u8FC5\u5728\u8BFE\u672C\u4E2D\u7684\u5206\u91CF\uFF0C\u5927\u591A\u6570\u4EBA\u5728\u8D70\u51FA\u6821\u95E8\uFF08\u786E\u5207\u8BF4\u6765\u662F\u4E0D\u518D\u4E0A\u8BED\u6587\u8BFE\uFF09\u4E4B\u540E\u5C31\u518D\u4E5F\u6CA1\u8BFB\u8FC7\u4ED6\u7684\u4F5C\u54C1\u3002\u4F46\u4E0E\u6B64\u540C\u65F6\uFF0C\u9C81\u8FC5\u7684\u601D\u60F3\u548C\u827A\u672F\u9057\u4EA7\uFF0C\u968F\u7740\u6211\u4EEC\u6240\u5904\u4E16\u754C\u4E0D\u65AD\u6D8C\u73B0\u51FA\u7684\u660E\u6697\u98CE\u6CE2\uFF0C\u5728\u5168\u4E16\u754C\u8303\u56F4\u5185\u88AB\u4E0D\u65AD\u5730\u4FDD\u62A4\u548C\u73CD\u89C6\u3002</p><p>\u5728\u5F3A\u5236\u6027\u6559\u80B2\u7ED3\u675F\u4E4B\u540E\u56E0\u7F18\u9645\u4F1A\u5730\u53D1\u73B0\u4E86\u9C81\u8FC5\u7684\u8BF8\u591A\u9762\u5411\uFF0C\u662F\u6211\u5728\u8FC7\u5F80\u5341\u591A\u5E74\u8BFB\u4E66\u751F\u6DAF\u4E2D\u6700\u611F\u5E86\u5E78\u7684\u7ECF\u5386\u3002\u6211\u59CB\u7EC8\u8BA4\u4E3A\uFF0C\u9C81\u8FC5\u662F\u4E00\u4E2A\u6C38\u6052\u7684\u3001\u53EF\u4EE5\u4E0D\u65AD\u8FD4\u56DE\u5E76\u4ECE\u4E2D\u6C72\u53D6\u517B\u5206\uFF0C\u4E14\u65F6\u65F6\u8BA9\u4EBA\u611F\u5230\u4EB2\u5207\u7684\u670B\u53CB\u3002\u4F5C\u4E3A\u5F53\u4EE3\u4E2D\u56FD\u6700\u4E3A\u91CD\u8981\u7684\u827A\u672F\u5BB6\u4E4B\u4E00\uFF0C\u9648\u4E39\u9752\u7684\u89E3\u8BFB\u4E0D\u540C\u4E8E\u591A\u6570\u523B\u677F\u6666\u6DA9\u7684\u5B66\u9662\u4F5C\u54C1\u3002\u4EE5\u827A\u672F\u4E3A\u67A2\u7EBD\uFF0C\u4EE5\u6027\u60C5\u4E3A\u95E8\u5F84\uFF0C\u4ED6\u5F15\u9886\u6211\u4EEC\u76F4\u62B5\u201C\u6587\u5B66\u5BB6\u3001\u601D\u60F3\u5BB6\u3001\u9769\u547D\u5BB6\u201D\u80CC\u540E\u8840\u8089\u4E30\u6EE1\u7684\u9C81\u8FC5\u4E16\u754C\u3002</p><h2 id="\u300A\u5F53\u547C\u5438\u5316\u4E3A\u7A7A\u6C14\u300B-\u4FDD\u7F57\xB7\u5361\u62C9\u5C3C\u4EC0\u8457-\u6D59\u6C5F\u6587\u827A\u51FA\u7248\u793E2016\u5E74" tabindex="-1"><a class="header-anchor" href="#\u300A\u5F53\u547C\u5438\u5316\u4E3A\u7A7A\u6C14\u300B-\u4FDD\u7F57\xB7\u5361\u62C9\u5C3C\u4EC0\u8457-\u6D59\u6C5F\u6587\u827A\u51FA\u7248\u793E2016\u5E74" aria-hidden="true">#</a> \u300A\u5F53\u547C\u5438\u5316\u4E3A\u7A7A\u6C14\u300B\uFF0C\u4FDD\u7F57\xB7\u5361\u62C9\u5C3C\u4EC0\u8457\uFF0C\u6D59\u6C5F\u6587\u827A\u51FA\u7248\u793E2016\u5E74</h2><p>\u4FDD\u7F57\xB7\u5361\u62C9\u5C3C\u4EC0\u662F\u4ECE\u6587\u5B66\u8F6C\u5165\u751F\u7269\u5B66\u7684\u5947\u624D\uFF0C\u662F\u8036\u9C81\u5927\u5B66\u533B\u5B66\u9662\u6210\u7EE9\u4F18\u5F02\u7684\u535A\u58EB\u751F\uFF0C\u662F\u65AF\u5766\u798F\u533B\u9662\u5189\u5189\u5347\u8D77\u7684\u5B66\u672F\u65B0\u661F\uFF0C\u4E5F\u662F\u4E00\u540D\u7F55\u89C1\u7684\u572836\u5C81\u4E4B\u524D\u4FBF\u7F79\u60A3\u80BA\u764C\u7684\u60A3\u8005\u3002\u8FD9\u672C\u4E66\u662F\u4ED6\u751F\u547D\u7684\u7EDD\u54CD\u3002\u5728\u5176\u4E2D\u4F60\u8BFB\u4E0D\u5230\u4E00\u6BB5\u7CBE\u5F69\u4EBA\u751F\u621B\u7136\u800C\u6B62\u7684\u4E0D\u7518\u6216\u6028\u6068\uFF0C\u751A\u81F3\u8BFB\u4E0D\u5230\u4E1D\u6BEB\u7684\u7262\u9A9A\u3002\u9762\u5BF9\u547D\u8FD0\u5DE8\u5927\u7684\u4E0D\u516C\uFF0C\u4F5C\u8005\u4ECD\u7136\u7EC6\u81F4\u800C\u6E29\u67D4\u5730\u56DE\u6EAF\u81EA\u5DF1\u7684\u4E00\u751F\u3002\u4F60\u751A\u81F3\u4F1A\u60CA\u8BB6\u4E8E\u4E00\u4E2A\u77ED\u6682\u7684\u751F\u547D\u4E2D\u6240\u5305\u5BB9\u7684\u6D69\u701A\u3002\u80BA\u764C\u6240\u9020\u6210\u7684\u8EAF\u4F53\u4E0A\u7684\u75DB\u82E6\u6BCB\u5EB8\u8D58\u8A00\uFF0C\u6BCF\u5FF5\u53CA\u6B64\uFF0C\u4E66\u9875\u4E2D\u5B57\u91CC\u884C\u95F4\u7684\u606C\u6DE1\u3001\u4ECE\u5BB9\u5C31\u6108\u53D1\u8BA9\u4EBA\u550F\u5618\u3002</p><p>\u65F6\u81F3\u4ECA\u65E5\uFF0C\u6B7B\u4EA1\u4ECD\u65E7\u662F\u6240\u6709\u751F\u547D\u90FD\u5C06\u8FCE\u6765\u7684\u7EC8\u6781\u547D\u8FD0\u3002\u6211\u5728\u201C\u7406\u89E3\u6B7B\u4EA1\u201D\u7684\u8BFE\u4E0A\u66FE\u7ECF\u5E0C\u671B\u540C\u5B66\u4EEC\u4ECE\u8FD9\u672C\u4E66\u91CC\u660E\u767D\u4E00\u4E2A\u9053\u7406\uFF1A\u65E0\u8BBA\u5728\u672A\u6765\u6211\u4EEC\u5C06\u8FCE\u6765\u4F55\u79CD\u8F89\u714C\uFF0C\u751F\u547D\u4ECD\u7136\u4F1A\u5728\u7EC8\u70B9\u5BA3\u5224\u65E0\u53EF\u9003\u907F\u7684\u201C\u5931\u8D25\u201D\u3002\u5FB7\u56FD\u54F2\u5B66\u5BB6\u6D77\u5FB7\u683C\u5C14\u8BF4\uFF0C\u53EA\u6709\u201C\u5411\u6B7B\u800C\u751F\u201D\uFF0C\u6211\u4EEC\u624D\u80FD\u771F\u6B63\u83B7\u5F97\u5BF9\u81EA\u8EAB\u5B58\u5728\u7684\u51DD\u89C6\u3002\u540C\u5B66\u4EEC\u9752\u6625\u5E74\u5C11\uFF0C\u6B7B\u4EA1\u9065\u8FDC\u800C\u6A21\u7CCA\u3002\u4E0D\u8FC7\uFF0C\u751F\u6B7B\u76F8\u4F9D\uFF0C\u8131\u79BB\u4E86\u5BF9\u201C\u6B7B\u4EA1\u201D\u7684\u8BA4\u8BC6\uFF0C\u773C\u4E0B\u6D3B\u7740\u7684\u610F\u4E49\u5C31\u96BE\u4EE5\u5F97\u5230\u5F7B\u5E95\u7684\u6F84\u6E05\u3002\u4FDD\u7F57\u7684\u4E66\u662F\u4E00\u6247\u7A97\uFF0C\u4ECE\u4E2D\u6211\u4EEC\u53EF\u4EE5\u770B\u89C1\uFF0C\u6B7B\u4EA1\u4E4B\u666F\u4E5F\u53EF\u4EE5\u660E\u4EAE\u3002</p><h2 id="\u300A\u5C0F\u738B\u5B50\u7684\u9886\u609F\u300B-\u5468\u4FDD\u677E\u8457-\u4E0A\u6D77\u4E09\u8054\u4E66\u5E972018\u5E74" tabindex="-1"><a class="header-anchor" href="#\u300A\u5C0F\u738B\u5B50\u7684\u9886\u609F\u300B-\u5468\u4FDD\u677E\u8457-\u4E0A\u6D77\u4E09\u8054\u4E66\u5E972018\u5E74" aria-hidden="true">#</a> \u300A\u5C0F\u738B\u5B50\u7684\u9886\u609F\u300B\uFF0C\u5468\u4FDD\u677E\u8457\uFF0C\u4E0A\u6D77\u4E09\u8054\u4E66\u5E972018\u5E74</h2><p>\u5723\u57C3\u514B\u82CF\u4F69\u91CC\u7684\u300A\u5C0F\u738B\u5B50\u300B\u662F\u5386\u4E45\u4E0D\u8870\u7684\u6210\u4EBA\u7AE5\u8BDD\uFF0C\u4E5F\u662F\u9999\u6E2F\u4E2D\u6587\u5927\u5B66\u653F\u6CBB\u5B66\u6559\u6388\u5468\u4FDD\u677E\u7684\u6700\u7231\u3002\u8FD9\u672C\u4E66\u662F\u5468\u5148\u751F\u5BF9\u300A\u5C0F\u738B\u5B50\u300B\u7684\u9886\u609F\uFF0C\u81F3\u771F\u81F3\u5207\uFF0C\u4FC3\u4EBA\u6DF1\u7701\u3002\u6211\u5728\u4E2D\u5927\u8BFB\u4E66\u65F6\uFF0C\u53C2\u52A0\u8FC7\u5468\u6559\u6388\u7684\u8BB2\u5EA7\u3001\u6C99\u9F99\uFF0C\u65C1\u542C\u8FC7\u4ED6\u5F00\u8BBE\u7684\u8BFE\u7A0B\u3002\u6709\u65F6\u4E0B\u5348\u997F\u4E86\uFF0C\u5306\u5306\u8DD1\u5230\u8054\u5408\u4E66\u9662\u7684\u996D\u5802\u91CC\u627E\u5403\u7684\uFF0C\u770B\u89C1\u4ED6\u88AB\u5B66\u751F\u4EEC\u7C07\u62E5\u7740\uFF0C\u6709\u8BF4\u6709\u7B11\u3002\u5728\u68EE\u4E25\u7684\u5B66\u672F\u4F53\u7CFB\u5185\uFF0C\u4ED6\u613F\u610F\u5E76\u4E14\u4E0D\u8F9E\u8F9B\u52B3\u5730\u505A\u4E00\u4E2A\u4F18\u79C0\u7684\u201C\u6559\u5E08\u201D\uFF0C\u575A\u5B88\u4E2D\u6587\u5927\u5B66\u516D\u5341\u5E74\u6765\u5173\u5207\u56FD\u65CF\u547D\u8FD0\u4E16\u9053\u4EBA\u5FC3\u7684\u4F20\u7EDF\u3002</p><p>\u300A\u5C0F\u738B\u5B50\u300B\u7684\u6838\u5FC3\u5728\u8FDE\u63A5\u4E07\u7269\u4E4B\u7231\uFF0C\u5B83\u4EE5\u7AE5\u8BDD\u7684\u5F62\u5F0F\u63ED\u793A\u4E86\u4EBA\u7C7B\u4E4B\u201C\u7231\u201D\u7684\u54F2\u5B66\uFF08\u751A\u81F3\u795E\u5B66\uFF09\u54C1\u8D28\u3002\u7231\u662F\u8DE8\u8D8A\u4E00\u5207\u754C\u7EBF\uFF0C\u662F\u6FC0\u8FDB\u5730\u5C06\u81EA\u6211\u6295\u8BF8\u4E8E\u4ED6\u4EBA\u7684\u751F\u547D\u4E4B\u4E2D\u3002\u8FD9\u672C\u300A\u9886\u609F\u300B\u65E2\u6709\u5BF9\u539F\u4E66\u6587\u672C\u7684\u5256\u6790\uFF0C\u4E5F\u6709\u5728\u6B64\u4E4B\u4E0A\u5BF9\u5F53\u4EE3\u4EBA\u7C7B\u751F\u5B58\u5904\u5883\u7684\u5FAE\u8A00\u5927\u4E49\u3002\u5B83\u53EF\u8D35\u5730\u4F53\u73B0\u4E86\u4E00\u4E2A\u4E2D\u5E74\u77E5\u8BC6\u5206\u5B50\uFF0C\u5728\u7EB7\u6270\u4E16\u4E8B\u3001\u65E0\u804A\u58EB\u6797\u7684\u538B\u6291\u4E4B\u4E0B\uFF0C\u4ECD\u5BF9\u4E00\u672C\u7AE5\u8BDD\u3001\u4E00\u4F17\u5B66\u751F\u3001\u4E00\u4E2A\u65F6\u4EE3\u4FDD\u6301\u7740\u5B69\u7AE5\u822C\u7684\u70ED\u60C5\u3002</p><h2 id="\u300A\u68B5\u9AD8\u4F20\u300B-\u53F2\u8482\u82AC\xB7\u5948\u83F2\u3001\u683C\u96F7\u9AD8\u91CC\xB7\u6000\u7279\xB7\u53F2\u5BC6\u65AF\u8457-\u8BD1\u6797\u51FA\u7248\u793E2015\u5E74\u7248" tabindex="-1"><a class="header-anchor" href="#\u300A\u68B5\u9AD8\u4F20\u300B-\u53F2\u8482\u82AC\xB7\u5948\u83F2\u3001\u683C\u96F7\u9AD8\u91CC\xB7\u6000\u7279\xB7\u53F2\u5BC6\u65AF\u8457-\u8BD1\u6797\u51FA\u7248\u793E2015\u5E74\u7248" aria-hidden="true">#</a> \u300A\u68B5\u9AD8\u4F20\u300B\uFF0C\u53F2\u8482\u82AC\xB7\u5948\u83F2\u3001\u683C\u96F7\u9AD8\u91CC\xB7\u6000\u7279\xB7\u53F2\u5BC6\u65AF\u8457\uFF0C\u8BD1\u6797\u51FA\u7248\u793E2015\u5E74\u7248</h2><p>\u8BF4\u68B5\u9AD8\u662F\u73B0\u4EE3\u827A\u672F\u7684\u56FE\u817E\u5E94\u4E0D\u4E3A\u8FC7\uFF0C\u9E22\u5C3E\u82B1\u3001\u5411\u65E5\u8475\u5370\u6210\u65E5\u5386\u724C\u3001\u51B0\u7BB1\u8D34\uFF0C\u5356\u5230\u6EE1\u4E16\u754C\u3002\u4E0E\u540C\u8F88\u5DE8\u5320\u76F8\u6BD4\uFF0C\u68B5\u9AD8\u5728\u6DF1\u9083\u7684\u7F8E\u5B66\u4E4B\u4E0A\u8986\u76D6\u4E86\u4E00\u5C42\u70ED\u70C8\u800C\u8BA9\u4EBA\u4EB2\u8FD1\u7684\u660E\u4EAE\u3002\u8D29\u592B\u8D70\u5352\uFF0C\u90FD\u53EF\u4EE5\u7231\u68B5\u9AD8\uFF0C\u7231\u4ED6\u7EDA\u4E3D\u7684\u9EC4\u3001\u6B23\u559C\u7684\u84DD\uFF0C\u4E30\u9976\u7684\u7D2B\uFF0C\u7231\u4ED6\u7684\u4E16\u754C\u6709\u4E00\u79CD\u7F8E\u597D\u7684\u76F4\u767D\u3002</p><p>\u767E\u5E74\u6765\uFF0C\u90A3\u4E48\u591A\u4EBA\u7231\u8BFB\u68B5\u9AD8\u7684\u4F20\u8BB0\u3002\u4ED6\u843D\u9B44\u60E8\u6DE1\u7684\u4E00\u751F\uFF0C\u6CA1\u6709\u7559\u4E0B\u4EFB\u4F55\u59A5\u534F\u3001\u53D8\u8282\u3001\u9022\u8FCE\u3001\u9003\u8131\u7684\u7A7A\u95F4\u3002\u4ED6\u7684\u7EAF\u7CB9\u6E90\u4E8E\u7A7A\u4E4F\u5176\u8EAB\u7684\u547D\u8FD0\u6349\u5F04\uFF0C\u800C\u9020\u5316\u4E5F\u7EC8\u7A76\u6210\u5168\u4E86\u4ED6\u4E00\u5C18\u4E0D\u67D3\u7684\u7EAF\u51C0\u5FC3\u7075\u3002\u6709\u4E00\u5E74\u56FD\u5916\u5267\u96C6\u4E2D\u8BA9\u8FD9\u4E2A\u5272\u6389\u8033\u6735\u7684\u7537\u4EBA\u6B7B\u800C\u590D\u751F\uFF0C\u4EB2\u4E34\u4E8C\u5341\u4E00\u4E16\u7EAA\u7684\u535A\u7269\u9986\uFF0C\u770B\u81EA\u5DF1\u7684\u4F5C\u54C1\u524D\u5B69\u5B50\u4EEC\u9759\u9759\u4E34\u6479\uFF0C\u60C5\u4FA3\u4EEC\u5408\u5F71\u7559\u5FF5\uFF0C\u6CEA\u6D41\u6EE1\u9762\u3002\u98CE\u683C\u8FED\u4EE3\uFF0C\u5927\u5E08\u661F\u79FB\uFF0C\u4F46\u827A\u672F\u7684\u672C\u8D28\u4ECD\u5728\u4E8E\u5C06\u6211\u4EEC\u5E26\u5411\u67D0\u4E9B\u4E0D\u53EF\u8A00\u8BF4\u7684\u6C38\u6052\u4E4B\u5730\u3002</p><h2 id="\u300A\u6708\u4EAE\u548C\u516D\u4FBF\u58EB\u300B-\u6BDB\u59C6\u8457-\u4E0A\u6D77\u8BD1\u6587\u51FA\u7248\u793E2016\u5E74\u7248-\u8BD1\u8005\u5085\u60DF\u6148" tabindex="-1"><a class="header-anchor" href="#\u300A\u6708\u4EAE\u548C\u516D\u4FBF\u58EB\u300B-\u6BDB\u59C6\u8457-\u4E0A\u6D77\u8BD1\u6587\u51FA\u7248\u793E2016\u5E74\u7248-\u8BD1\u8005\u5085\u60DF\u6148" aria-hidden="true">#</a> \u300A\u6708\u4EAE\u548C\u516D\u4FBF\u58EB\u300B\uFF0C\u6BDB\u59C6\u8457\uFF0C\u4E0A\u6D77\u8BD1\u6587\u51FA\u7248\u793E2016\u5E74\u7248\uFF08\u8BD1\u8005\u5085\u60DF\u6148\uFF09</h2><p>\u8FD9\u672C\u4E66\u592A\u6709\u540D\uFF0C\u4EE5\u81F3\u4E8E\u628A\u5B83\u653E\u8FDB\u4E66\u5355\uFF0C\u5DF2\u7ECF\u6709\u843D\u4FD7\u5957\u4E4B\u5ACC\u3002\u66F4\u6709\u751A\u8005\uFF0C\u4EE5\u4ECA\u65E5\u773C\u5149\u5BA1\u89C6\uFF0C\u4E00\u4E2A\u4E2D\u5E74\u7537\u4EBA\u629B\u5BB6\u820D\u4E1A\u8FFD\u6C42\u827A\u672F\u8FD9\u7C7B\u865A\u5E7B\u68A6\u60F3\uFF0C\u53EF\u8C13\u6E23\u7537\u3002\u4F46\u5047\u82E5\u8981\u52C9\u5F3A\u4E3A\u4E4B\u8FA9\u62A4\uFF0C\u6211\u4E0D\u4F1A\u62AC\u51FA\u539F\u578B\u4FDD\u7F57\xB7\u9AD8\u66F4\u7684\u827A\u672F\u6210\u5C31\u4F5C\u864E\u76AE\u3002\u4F5C\u4E3A\u82B8\u82B8\u4F17\u751F\uFF0C\u6211\u4EEC\u90FD\u4E0D\u514D\u5728\u4E00\u751F\u4E2D\u7684\u67D0\u4E9B\u8282\u70B9\u80C6\u602F\u8BA4\u6002\uFF0C\u201C\u7B97\u4E86\u7B97\u4E86\uFF0C\u5C31\u542C\u6211\u7238\u7684\u53BB\u8BFB\u4E2A\u91D1\u878D\uFF0C\u597D\u6B79\u6323\u94B1\u591A\u201D\u3002\u4F46\u66FE\u6709\u7684\u5F02\u60F3\u5929\u5F00\u54EA\u4F1A\u8F7B\u6613\u7184\u706B\uFF1F\u65E0\u6570\u4E07\u7C41\u4FF1\u5BC2\u7684\u5348\u591C\uFF0C\u626A\u5FC3\u95EE\u201C\u8981\u662F\u5F53\u521D\u5982\u4F55\u5982\u4F55\uFF0C\u73B0\u5728\u53C8\u8BE5\u5982\u4F55\u5982\u4F55\u201D\u3002\u611F\u8C22\u6BDB\u59C6\uFF0C\u628A\u5168\u4EBA\u7C7B\u7684\u4E0D\u7518\u5FC3\u4EA4\u7ED9\u4E00\u4E2A\u7231\u827A\u672F\u7684\u6DF7\u86CB\u3002\u6211\u4EEC\u6068\u4ED6\u3001\u9A82\u4ED6\uFF0C\u4E5F\u7231\u4ED6\u3002</p><p>\u5E74\u9F7F\u5F92\u589E\uFF0C\u6211\u6108\u52A0\u5BBD\u5BB9\u548C\u7406\u89E3\u4E66\u91CC\u90A3\u4F4D\u601D\u7279\u91CC\u514B\u5170\u5FB7\u3002\u66FE\u7ECF\u542C\u8FC7\u7684\u6B4C\u3001\u7231\u8FC7\u7684\u753B\u3001\u4E3A\u4E4B\u54ED\u8FC7\u7B11\u8FC7\u7684\u7535\u5F71\u620F\u5267\uFF0C\u90FD\u662F\u5EB8\u5EB8\u65E5\u5E38\u4E4B\u4E2D\u4E3A\u4E00\u9053\u9003\u9038\u7684\u51FA\u53E3\u3002\u6709\u65F6\u5411\u8FDC\u65B9\uFF0C\u6709\u65F6\u5411\u6545\u4E61\u3002\u5728\u4E00\u4E2A\u5DE5\u4E1A\u6587\u660E\u65E9\u5DF2\u6DF1\u5165\u9AA8\u9AD3\u3001\u4EF7\u503C\u7406\u6027\u65E0\u53EF\u64BC\u52A8\u7684\u5E74\u4EE3\uFF0C\u827A\u672F\u4ECD\u7136\u662F\u91CD\u8981\u7684\uFF0C\u4ECD\u7136\u6484\u4EBA\u5FC3\u9B44\uFF0C\u50CF\u82CD\u832B\u5927\u6D77\u4E0A\u6D6E\u8D77\u585E\u58EC\u7684\u6B4C\u3002</p><h2 id="\u300A\u4ED6\u4EEC\u8BF4-\u6211\u662F\u5E78\u8FD0\u7684\u300B-\u827E\u4E3D\u65AF\xB7\u897F\u4F2F\u5FB7\u8457-\u5317\u4EAC\u8054\u5408\u51FA\u7248\u516C\u53F82016\u5E74\u7248" tabindex="-1"><a class="header-anchor" href="#\u300A\u4ED6\u4EEC\u8BF4-\u6211\u662F\u5E78\u8FD0\u7684\u300B-\u827E\u4E3D\u65AF\xB7\u897F\u4F2F\u5FB7\u8457-\u5317\u4EAC\u8054\u5408\u51FA\u7248\u516C\u53F82016\u5E74\u7248" aria-hidden="true">#</a> \u300A\u4ED6\u4EEC\u8BF4\uFF0C\u6211\u662F\u5E78\u8FD0\u7684\u300B\uFF0C\u827E\u4E3D\u65AF\xB7\u897F\u4F2F\u5FB7\u8457\uFF0C\u5317\u4EAC\u8054\u5408\u51FA\u7248\u516C\u53F82016\u5E74\u7248</h2><p>\u5927\u6D0B\u5F7C\u5CB8\u7684#me too\u98CE\u6F6E\u7EC8\u4E8E\u5E2D\u5377\u6B64\u5CB8\uFF0C\u4E0D\u5C11\u56FD\u4EBA\u6025\u5306\u5306\u5730\u54C0\u53F9\u201C\u7F51\u7EDC\u66B4\u529B\u201D\uFF0C\u75DB\u65A5\u201C\u653F\u6CBB\u6B63\u786E\u201D\u3002\u4F3C\u4E4E\u5343\u4E07\u5973\u6027\u6210\u957F\u7ECF\u5386\u4E2D\u96BE\u4E8E\u542F\u9F7F\u3001\u523B\u9AA8\u94ED\u5FC3\u7684\u4F24\uFF0C\u63ED\u5F00\u6765\u8FD8\u5FC5\u987B\u6E29\u826F\u606D\u4FED\u3001\u5C0F\u5FC3\u7FFC\u7FFC\u3002</p><p>\u827E\u4E3D\u65AF\xB7\u897F\u4F2F\u5FB7\u662F\u7F8E\u56FD\u8457\u540D\u5C0F\u8BF4\u5BB6\u96F7\u8499\u5FB7\xB7\u5361\u4F5B\u7684\u5B66\u751F\uFF0C\u4E5F\u662F\u4E00\u540D\u6027\u4FB5\u72AF\u7684\u53D7\u5BB3\u8005\u3002\u4E0E\u5927\u591A\u6570\u6709\u7C7B\u4F3C\u7ECF\u5386\u7684\u4EBA\u4E0D\u540C\uFF0C\u5979\u51ED\u501F\u51FA\u8272\u7684\u6587\u5B66\u5929\u8D4B\uFF0C\u4EE5\u7EC6\u817B\u800C\u53C8\u9690\u5FCD\u7684\u7B14\u6CD5\u5C06\u4F24\u53E3\u63ED\u5F00\uFF0C\u8D70\u5411\u65B0\u751F\u3002\u8FD9\u662F\u4E00\u672C\u6CE8\u5B9A\u4F7F\u4EBA\u611F\u5230\u4E0D\u5FEB\u7684\u4E66\u3002\u4F46\u51ED\u501F\u4ED6\u4EBA\u7684\u53D9\u8FF0\u800C\u8FDB\u5165\u4EBA\u7C7B\u82E6\u96BE\u4E16\u754C\uFF0C\u6070\u662F\u9605\u8BFB\u81EA\u8EAB\u65E0\u53EF\u53D6\u4EE3\u7684\u4EF7\u503C\u4E4B\u4E00\u3002\u77E5\u6089\u82E6\u75DB\u5E76\u975E\u4E3A\u4E86\u628A\u73A9\u4ED6\u4EBA\u7684\u9690\u79C1\uFF0C\u800C\u662F\u4E0D\u65AD\u786E\u4FE1\u67D0\u4E9B\u4EBA\u9053\u4EF7\u503C\u7684\u610F\u4E49\u3002\u6784\u5EFA\u4E00\u4E2A\u6027\u522B\u5E73\u7B49\u3001\u4E24\u6027\u53CB\u5584\u7684\u793E\u4F1A\uFF0C\u5173\u4E4E\u6211\u4EEC\u6BCF\u4E00\u4E2A\u4EBA\u7684\u798F\u7949\uFF0C\u800C\u8FD9\u4E00\u5207\u5728\u4E2D\u56FD\u7684\u5B9E\u73B0\u4ECD\u65E7\u6709\u8D56\u4E8E\u540C\u5B66\u4EEC\u8FD9\u4E00\u4EE3\u65B0\u4EBA\u7684\u81EA\u89C9\u548C\u52AA\u529B\u3002</p><p>\uFF08\u897F\u4F2F\u5FB7\u8FD8\u6709\u4E00\u672C\u540C\u6837\u5173\u6CE8\u6027\u66B4\u529B\u7684\u5C0F\u8BF4\u300A\u53EF\u7231\u7684\u9AA8\u5934\u300B\uFF0C\u63A2\u8BA8\u4E86\u66F4\u4E3A\u6DF1\u9083\u7684\u751F\u6B7B\u95EE\u9898\uFF0C\u540C\u6837\u503C\u5F97\u4E00\u8BFB\u3002\uFF09</p><h2 id="\u300A\u5927\u80CC\u53DB-\u79D1\u5B66\u4E2D\u7684\u6B3A\u8BC8\u300B-\u970D\u52D2\u65AF\xB7\u8D3E\u5FB7\u68EE\u8457-\u4E09\u8054\u4E66\u5E972018\u5E74\u7248" tabindex="-1"><a class="header-anchor" href="#\u300A\u5927\u80CC\u53DB-\u79D1\u5B66\u4E2D\u7684\u6B3A\u8BC8\u300B-\u970D\u52D2\u65AF\xB7\u8D3E\u5FB7\u68EE\u8457-\u4E09\u8054\u4E66\u5E972018\u5E74\u7248" aria-hidden="true">#</a> \u300A\u5927\u80CC\u53DB\uFF1A\u79D1\u5B66\u4E2D\u7684\u6B3A\u8BC8\u300B\uFF0C\u970D\u52D2\u65AF\xB7\u8D3E\u5FB7\u68EE\u8457\uFF0C\u4E09\u8054\u4E66\u5E972018\u5E74\u7248</h2><p>\u6CB8\u6CB8\u626C\u626C\u7684\u201C\u97E9\u6625\u96E8\u4E8B\u4EF6\u201D\u7EC8\u4EE5\u4E00\u7EB8\u201C\u672A\u53D1\u73B0\u4E3B\u89C2\u9020\u5047\u201D\u7684\u8C03\u67E5\u7ED3\u8BBA\u4F5C\u7ED3\u3002\u5BF9\u4E2D\u56FD\u79D1\u5B66\u754C\u800C\u8A00\uFF0C\u8FD9\u79CD\u906E\u63A9\u518D\u4E00\u6B21\u66B4\u9732\u4E86\u89C4\u8303\u7F3A\u5931\u3001\u76D1\u7BA1\u4E0D\u529B\u4EE5\u53CA\u5C1A\u672A\u5F62\u6210\u884C\u4E1A\u81EA\u5F8B\u6240\u5B73\u751F\u7684\u75C5\u7076\u3002\u4EE5\u8FFD\u6C42\u5BA2\u89C2\u771F\u7406\u4E3A\u5DF1\u4EFB\u7684\u79D1\u5B66\uFF0C\u4ECE\u4E0D\u662F\u60AC\u6D6E\u5728\u771F\u7A7A\u4E4B\u4E2D\uFF0C\u5B83\u540C\u6837\u5D4C\u5165\u4E8E\u7279\u5B9A\u7684\u4EF7\u503C\u4F53\u7CFB\u4E0E\u6587\u5316\u89C2\u5FF5\u3002</p><p>\u4E0D\u5E78\u7684\u662F\uFF0C\u79D1\u5B66\u7684\u8FDB\u6B65\u53F2\u51E0\u4E4E\u540C\u6837\u662F\u4E00\u90E8\u5145\u6EE1\u6B3A\u8BC8\u548C\u865A\u4F2A\u7684\u5386\u53F2\u3002\u5BF9\u7814\u7A76\u4EBA\u5458\u800C\u8A00\uFF0C\u9700\u8981\u4E0E\u4E4B\u6597\u4E89\u7684\u5E76\u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u4E2A\u667A\u8BC6\u4E0A\u7684\u6311\u6218\u3001\u624B\u6BB5\u4E0A\u7684\u5C40\u9650\uFF0C\u8FD8\u6709\u4EBA\u7C7B\u81EA\u8EAB\u7684\u8D2A\u6B32\u548C\u7F3A\u61BE\u3002\u79D1\u5B66\u7814\u7A76\u8005\u65E9\u5C31\u4E0D\u518D\u662F\u9752\u706F\u9EC4\u5377\u6DF1\u85CF\u4E8E\u7B80\u964B\u5B9E\u9A8C\u5BA4\u91CC\u7684\u9690\u8005\uFF0C\u4ED6\u4EEC\u540C\u6837\u9762\u5BF9\u7740\u4E16\u4FD7\u610F\u4E49\u4E0A\u7684\u540D\u5229\u8BF1\u60D1\u3002\u56E0\u6B64\uFF0C\u5728\u5B66\u672F\u751F\u6DAF\u7684\u8D77\u6B65\u9636\u6BB5\u5C31\u5EFA\u7ACB\u8D77\u7262\u56FA\u7684\u81EA\u7701\u610F\u8BC6\uFF0C\u4ECE\u6BCF\u4E00\u4EFD\u4F5C\u4E1A\u6BCF\u4E00\u7BC7\u62A5\u544A\u505A\u8D77\uFF0C\u4E0D\u5B58\u4FA5\u5E78\u4E4B\u5FC3\uFF0C\u4E0D\u8D8A\u96F7\u6C60\u4E00\u6B65\uFF0C\u5BF9\u5404\u4F4D\u540C\u5B66\u800C\u8A00\u662F\u5173\u4E4E\u672A\u6765\u4E4B\u8DEF\u7684\u5FC5\u4FEE\u529F\u8BFE\u3002\u5E0C\u671B\u8FD9\u90E8\u5145\u65A5\u7740\u8352\u8BDE\u7684\u5386\u53F2\uFF0C\u80FD\u4F5C\u4E3A\u5927\u5BB6\u4FEE\u8EAB\u4E4B\u955C\u9274\u3002</p><p>\u672C\u4E66\u5C5E\u4E8E\u4E09\u8054\u4E66\u5E97\u201C\u65B0\u77E5\u6587\u5E93\u201D\u7684\u4E00\u79CD\uFF0C\u8FD9\u5957\u4E1B\u4E66\u5927\u90FD\u5F15\u4ECB\u81EA\u897F\u65B9\u4E16\u754C\u6D41\u884C\u79D1\u666E\u7545\u9500\u4E66\uFF0C\u4ECA\u5E74\u590F\u5929\u521A\u521A\u6EE1100\u79CD\uFF0C\u5C24\u5176\u9002\u5408\u5927\u5B66\u751F\u9605\u8BFB\u3002</p><p><strong>\u4EE5\u4E0A\u63A8\u8350\u4E0E\u6240\u8C13\u201C\u6743\u5A01\u201D\u65E0\u5173\uFF0C\u5B83\u53EA\u662F\u4E00\u4E2A\u957F\u671F\u4EE5\u8BFB\u5199\u4E3A\u751F\u7684\u666E\u901A\u6559\u5E08\u7684\u4E00\u4EFD\u9080\u8BF7\u3002\u6211\u5F88\u5E86\u5E78\u81EA\u5DF1\u51FA\u751F\u5E76\u6210\u957F\u4E8E\u524D\u6570\u5B57\u65F6\u4EE3\uFF0C\u5728\u6211\u6F2B\u957F\u800C\u5B64\u72EC\u7684\u9752\u6625\u671F\uFF0C\u9605\u8BFB\u8BA9\u6211\u5EF6\u5C55\u4E86\u6709\u9650\u7684\u751F\u547D\u3002\u6211\u5E0C\u671B\u81EA\u5DF1\u66FE\u89C1\u8BC6\u7684\u98CE\u666F\uFF0C\u4E5F\u80FD\u8BA9\u540E\u6765\u8005\u9886\u7565\u3002\u6BCF\u4E00\u672C\u4E66\u90FD\u6709\u5176\u54C1\u683C\u548C\u7075\u9B42\uFF0C\u6BCF\u4E00\u672C\u4E66\u4E0E\u6211\u4EEC\u7684\u76F8\u9047\u56E0\u800C\u4E5F\u5C31\u6709\u4E07\u822C\u5E7B\u5316\u53EF\u80FD\u3002\u4F60\u7231\u4E0A\u67D0\u4E00\u672C\u4E66\u7684\u65F6\u523B\uFF0C\u4E5F\u5C31\u662F\u4F60\u7231\u4E0A\u63A5\u4E0B\u6765\u65E0\u6570\u672C\u4E66\u7684\u65F6\u523B\u3002\u795D\u613F\u5927\u5BB6\u4ECE\u9605\u8BFB\u4E2D\u5F97\u6B22\u559C\u5B81\u9759\u3002</strong></p>',35),c=a("Copyright: "),l={href:"http://faculty.sustech.edu.cn/yuancg/",target:"_blank",rel:"noopener noreferrer"},u=a("\u8881\u957F\u5E9A"),_=a(", All rights reserved.");function f(x,b){const r=n("ExternalLinkIcon");return t(),d("div",null,[o,e("p",null,[e("em",null,[c,e("a",l,[u,s(r)]),_])])])}const g=h(p,[["render",f],["__file","index.html.vue"]]);export{g as default};
